import { React, useState } from "react";

import './LocationZone.css';

function LocationZone() {

    const [zones, setZones] = useState([])
    const cityId = localStorage.getItem('cityID');

    if (cityId !== undefined && cityId !== null) {
          //API
          alert(cityId)
      fetch('http://localhost:12911/api/MuyCercano/Localidades?ciudadId=' + cityId)
      .then(response => response.json())
      .then(data =>  setZones(data.MCLocalidad))
      .catch(error => console.error(error));

      }


    
  //jsx
  return (

      <div class="field">
              <h3 className="titlesPagesD">
                  Zona
              </h3>
              <select id="cbxLocalidad" name="cbxLocalidad" className="ui search dropdown">
                  {
                  zones.map(zone => {
                      return <option key={ zone.Id } value={ zone.Id }>{ zone.Localidad}</option>
                  })
}
              </select>
              
          </div>
          );
        }
     export default LocationZone;
